import Vue from 'vue';
import VueI18n from 'vue-i18n';

Vue.use(VueI18n);

const messages = {
  en: {
    welcome: 'Welcome',
	home: 'Home',
	about: 'About',
	login: 'Login',
	enter_username: 'Enter your username',
	enter_password: 'Enter your password',
	create_account: 'Create an account',
  },
  fr: {
    welcome: 'Bienvenue',
	home: 'Accueil',
	about: 'À propos',
	login: 'Connection',
	enter_username: 'Entrez votre dawdadawdwdawdadawdawdawdawadwnom d\'utilisateur',
	enter_password: 'Entrez votre mot de passe',
	create_account: 'Créer un compte',
  },
};

const i18n = new VueI18n({
  locale: 'fr',
  fallbackLocale: 'en',
  messages,
});

export default i18n;
