import Vue from 'vue';
import VueI18n from 'vue-i18n';

// Activer Vue i18n
Vue.use(VueI18n);

// Définir les messages de traduction
const messages = {
  en: {
    welcome: 'Welcome',
    // d'autres traductions...
  },
  fr: {
    welcome: 'Bienvenue',
    // d'autres traductions...
  },
  // Ajoutez plus de langues ici...
};

// Créer l'instance Vue i18n
const i18n = new VueI18n({
  locale: 'en', // Langue par défaut
  fallbackLocale: 'en', // Langue de secours
  messages, // Messages de traduction
});

export default i18n;
